import * as THREE from "three"
import { useRef, useState, Suspense, useEffect, useCallback } from 'react'
import { Canvas, useThree,useFrame} from "react-three-fiber"
import { OrbitControls  } from 'drei'
import { useFBX } from '@react-three/drei/useFBX'
import Layout from '../components/layout'
import { EffectComposer, DepthOfField, Bloom, Noise } from '@react-three/postprocessing'
import UseFBXScene from '../components/baby'
import Loader from '../components/loader'
import React from 'react'





function Bg() {
  const { viewport } = useThree()
  const max = Math.max(viewport.width, viewport.height)
  const ref = useRef()
  useFrame((state, delta) => (ref.current.time += delta))
  return (
    <mesh scale={[max, max, 1]}>
      <planeBufferGeometry />
      <colorShiftMaterial ref={ref} />
    </mesh>
  )
}




const Index = () => {

  const [image, setImage] = useState(null)
  const [isScreenshot, setScreenshot] = useState(false)
  const [isSnap, setIsSnap] = useState(false)
  const [random, setRandom] = useState(1)

  useEffect(()=>{
    console.log('image has changed!')
    if(image !== null){
      setImage(image)
      console.log('image', image)
      // setScreenshot(false)
      // setIsSnap(true)
    }
  },[image])

  useEffect(()=>{
     setRandom(parseInt(Math.random()*(random-1)))
  },[])



  const Snapshot = useCallback(() =>{
    setScreenshot(true)

  })


  return (
   <Layout>
     <div className="three">

     <div className={isSnap ? 'modalImage' : 'modalImageHidden'}>
       <img src={image} width="100%" height="100%"/>
     </div>

      <Canvas camera={{fov: 75, position: [0, 10, 7] }} id="canvasik" 
       invalidateFrameloop={true}
        onCreated={()=>{console.log('loaded')}}
      >
        <ambientLight intensity={22.2} />
        <pointLight position={[40, 40, 40]} />
        <OrbitControls enableZoom={false}/>
        <Suspense fallback={null}>
          <UseFBXScene setImage={setImage} isScreenshot={isScreenshot} random={random}/>    
        </Suspense>
        <EffectComposer>
        <DepthOfField focusDistance={1.0} focalLength={0.02} bokehScale={0.2} height={800} />
        <Bloom luminanceThreshold={2.0} luminanceSmoothing={0.5} height={800} />
        <Noise opacity={0.15} />

      </EffectComposer>

      </Canvas>
        <button className="snapshot" onClick={(e)=>{e.preventDefault(); Snapshot()}}>Snap!</button>
     </div>
    </Layout>
  )
}

export default React.memo(Index)
