import { useRef, useEffect, useState } from 'react'
import { Canvas, useThree} from "react-three-fiber"
import Layout from '../components/layout'
import OneContainer from '../components/oneContainer'
import Two from '../components/two'
import Three from '../components/three'
import Four from '../components/four'
import Five from '../components/five'
import Six from '../components/six'
import React from 'react'
import { motion, AnimatePresence } from "framer-motion"

function Camera(props) {
  const ref = useRef()
  const { setDefaultCamera } = useThree()
  useEffect(() => void setDefaultCamera(ref.current), [])
  return <perspectiveCamera ref={ref} {...props} />
}


const Index = () => {  
  const [objectDisplay, setObjectDisplay] = useState(1)

  const NextObject = () =>{
    if(objectDisplay<=5){
    setObjectDisplay(objectDisplay+1)
    } else {
      setObjectDisplay(objectDisplay-1)
    }
  }

  const objects = [<OneContainer clickOne={NextObject}/>, <Two clickOne={NextObject}/>, <Three clickOne={NextObject}/>, <Four clickOne={NextObject}/>, <Five clickOne={NextObject}/>, <Six clickOne={NextObject}/>]


  return (
   <Layout>
     <AnimatePresence>
        <motion.div className="three"   initial={{ y: 300, opacity: 0 }}
         transition={{ duration: 2.5 }}
      animate={{ y: 0, opacity: 1 }}
      exit={{ y: -300, opacity: 0 }}>
            {objects[objectDisplay]}  
        </motion.div>
     </AnimatePresence>
    </Layout>
  )
}

export default Index


