import { useState, useEffect, useRef } from 'react'
import Layout from '../components/layout'
import OneContainer from '../components/oneContainer'
import TwoContainer from '../components/twoContainer'
import ThreeContainer from '../components/threeContainer'
import FourContainer from '../components/fourContainer'
import FiveContainer from '../components/fiveContainer'
import SixContainer from '../components/sixContainer'
import React from 'react'
import Introduction from '../components/text/introduction'
import One from '../components/text/1'
import Two from '../components/text/2'
import Three from '../components/text/3'
import Four from '../components/text/4'
import Five from '../components/text/5'

import { motion, AnimatePresence, useElementScroll } from "framer-motion"
// import ReactAudioPlayer from 'react-audio-player';

import { PlayButton, Timer, Progress } from 'react-soundplayer/components';

// it's just an alias for `withSoundCloudAudio` but makes code clearer
import { withCustomAudio } from 'react-soundplayer/addons';

// audio source
const streamUrl = "/audio/1.aac";

// some track meta information
const trackTitle = 'Great song by random artist';


const AWSSoundPlayer = withCustomAudio(props => {
  const { trackTitle } = props;

  return (
    <div className='player-container'>
      <PlayButton {...props} />
      <Progress className={'progressbar'}/>
      <h5>{trackTitle}</h5>
    </div>
  );
});


const Index = () => {  
  const [objectDisplay, setObjectDisplay] = useState(0)
  const [scrollMax, setScrollMax] = useState(0)
  const [count, setCount] = useState(1)


  const ref = useRef(null)
  // const { scrollYProgress } = useElementScroll(ref)


  const NextObject = (number) =>{

    setObjectDisplay(number)

  }

  const Animate = () => {
    // console.log('scroll',  scrollMax,  ref.current.scrollTop)
    // if(ref.current){
    //   ref.current.scrollTop =  ref.current.scrollTop+count;
    //   // console.log(ref.current.scrollTop)
    //   if(ref.current.scrollTop > 250) {
    //     console.log('more!', ref.current.scrollTop)
    //     setCount(-1)
    //   }
    //   if(ref.current.scrollTop === 0) {
    //     setCount(1)
    //   }
    //   requestAnimationFrame(Animate)
    // }
  }

  useEffect(()=>{
    console.log('scroll',  ref.current.scrollHeight, ref.current.clientHeight)
    setScrollMax(ref.current.clientHeight)
    requestAnimationFrame(Animate)
  },[])

  const titles = ['INTRO', 'Active agent in Evolution', 'MultiUnity', 'ANDROGYNOUS WHOLENESS', 'Horizon of Eternity',  'SUBJECT TO COSMIC LAW']


  const descriptions = [<Introduction/>, <One/>, <Two/>, <Three/>, <Four/>, <Five/>]

  //Two is Homepage
  //First is First
  //Second is Second
  //

  const objects = [ <TwoContainer clickOne={NextObject} />,
                    <OneContainer clickOne={NextObject}/>,
                  <ThreeContainer clickOne={NextObject}/>,
                  <FourContainer clickOne={NextObject} />,
                  <FiveContainer clickOne={NextObject}/>,
                  <SixContainer clickOne={NextObject}/>]

  const objectsTitles = [ <TwoContainer clickOne={NextObject} title={'COSMIST FUTURISM'} />,
    <OneContainer clickOne={NextObject} title={'Active agent in Evolution'} number={1}/>,
  <ThreeContainer clickOne={NextObject} title={'MultiUnity'}/>,
  <FourContainer clickOne={NextObject} title={'ANDROGYNOUS WHOLENESS'}/>,
  <FiveContainer clickOne={NextObject} title={'Horizon of Eternity'}/>,
  <SixContainer clickOne={NextObject} title={'SUBJECT TO COSMIC LAW'}/>]           


  return (
   <Layout>
    <main>
      <div className='object-main'>
      <div className='object-text'>
       {descriptions[objectDisplay]}
        <div className='object-text-three'>
        {objects[objectDisplay]}
        </div>
      </div>
          <div className='object-three' ref={ref}>
            <motion.div>


            <AnimatePresence>
              {objectsTitles.map((object, index)=>{
                return(
                  <React.Fragment key={index}>
                  {object}
                  </React.Fragment>
                  )
              })}
            </AnimatePresence>


            </motion.div>
          </div>

        </div>
        <div className="footer">
              <div className="audio">
                  <AWSSoundPlayer
                  trackTitle='AUDIO'
                  streamUrl={streamUrl}
                  preloadType="auto" />
                </div>
            <div className="card-footer">
              <div><h5>COSMIST FUTURISM</h5></div>
              <div><h5>PRoject developed by Petra Eros and Kirill Noskov</h5></div>
            </div>
        </div>
      </main>
    </Layout>
  )
}

export default Index
