import * as THREE from "three"
import { useRef, useState, Suspense, useEffect, useCallback } from 'react'
import { Canvas, useThree,useFrame} from "react-three-fiber"
import { OrbitControls  } from 'drei'
import { useFBX } from '@react-three/drei/useFBX'
import Layout from '../components/layout'
import { EffectComposer, DepthOfField, Bloom, Noise } from '@react-three/postprocessing'
import UseFBXScene from '../components/baby'
import React from 'react'
import { useRouter } from 'next/router'



function Camera(props) {
  const ref = useRef()
  const { setDefaultCamera } = useThree()
  useEffect(() => void setDefaultCamera(ref.current), [])
  return <perspectiveCamera ref={ref} {...props} />
}


const Index = () => {


  const router = useRouter()

    const clickOne = () =>{

      router.push('/one')
    }
    const clickTwo = () =>{

      router.push('/two')
    }
    const clickThree = () =>{

      router.push('/three')
    }
    const clickFour = () =>{

      router.push('/four')
    }
    const clickFive = () =>{

      router.push('/five')
    }
    const clickSix = () =>{

      router.push('/six')
    }

  return (
   <Layout>
     <div className="three">
      <Canvas>
         <Camera position={[0, 0, 15]} />
        <ambientLight intensity={1.0} />
        <pointLight position={[40, 40, 40]} />
        <Suspense fallback={null}>
          <UseFBXScene clickOne={clickOne} clickTwo={clickTwo} clickThree={clickThree} clickFour={clickFour} clickFive={clickFive} clickSix={clickSix}/>    
        </Suspense>
      </Canvas>
     </div>
    </Layout>
  )
}

export default Index


