import React from 'react'
import { useRef, Suspense, useState} from 'react'
import { useThree, useFrame} from "react-three-fiber"
import { useFBX } from '@react-three/drei/useFBX'



const FBX = ({click, object}) => {


    const mesh = useRef()
    let fbx = useFBX(`/models/${object}.fbx`)
    const { 
      gl,             
      canvas,              
    } = useThree()

    const [hovered, setHover] = useState(false)
    const [active, setActive] = useState(false)

    useFrame(() => {
      if(hovered){

        // mesh.current.rotation.x += 0.01
        mesh.current.rotation.y += 0.1
      }
    })




      return (

          <mesh
          {...fbx.children[0]}
          ref={mesh}
          position={[0, 0, 6]}
          scale={[0.1,0.1,0.1]}
          name="Object_0"
          rotation={[0, 0, 0]}
          onClick={e=>click()}
          onPointerOver={e => setHover(true)}
          onPointerOut={e => setHover(false)}
        >
        </mesh>

      )
  }



  const One = ({clickOne, object}) => {



    return (
      <Suspense callback={false}>
          <FBX click={clickOne} object={object}/>
        </Suspense>
    )
  }

  export default One
