import * as THREE from "three"
import React from 'react'
import { useRef, Suspense, useState} from 'react'
import { useThree, useFrame} from "react-three-fiber"
import { useFBX } from '@react-three/drei/useFBX'
import { Bloom } from '@react-three/postprocessing'



const OneFBX = () => {

    const mesh = useRef()
    let fbx = useFBX('/models/1.fbx')
    const { 
      gl,             
      canvas,              
    } = useThree()

    const [hovered, setHover] = useState(false)
    const [active, setActive] = useState(false)

    useFrame(() => {
      if(hovered){
        mesh.current.rotation.x = mesh.current.rotation.y += 0.01
      }
    })


      return (
        <mesh
        {...fbx.children[0]}
        ref={mesh}
        position={[0, -4, 0]}
        scale={[0.1,0.1,0.1]}
        name="Object_0"
        rotation={[0, 0, 0]}
        onClick={e => setActive(!active)}
        onPointerOver={e => setHover(true)}
        onPointerOut={e => setHover(false)}
      >
            {/* <meshBasicMaterial  attach="material" map={texture}/>   */}
      </mesh>
      )
  }
  const TwoFBX = () => {


    const mesh = useRef()
    let fbx = useFBX('/models/2.fbx')
    const { 
      gl,             
      canvas,              
    } = useThree()

    // useFrame(() => (mesh.current.rotation.x = mesh.current.rotation.y += 0.01))
    const [hovered, setHover] = useState(false)
    const [active, setActive] = useState(false)

    useFrame(() => {
      if(hovered){

        // mesh.current.rotation.x += 0.01
        mesh.current.rotation.y += 0.1
      }
    })

      return (


        <mesh
        onClick={()=>{alert('yo')}}
        {...fbx.children[0]}
        ref={mesh}
        position={[6, 0, 2]}
        scale={[0.1,0.1,0.1]}
        name="Object_0"
        onClick={e => setActive(!active)}
        onPointerOver={e => setHover(true)}
        onPointerOut={e => setHover(false)}
      >
            {/* <meshBasicMaterial  attach="material" map={texture}/>   */}
      </mesh>

      )
  }

  const ThreeFBX = () => {

    const mesh = useRef()
    let fbx = useFBX('/models/3.fbx')
    const { 
      gl,             
      canvas,              
    } = useThree()

    //  useFrame(() => {
    //    mesh.current.rotation.x = mesh.current.rotation.y += 0.1
    //    console.log(mesh.current.rotation)
    //  })

      return (
        <mesh
        {...fbx.children[0]}
        ref={mesh}
        position={[12, 0, 4]}
        scale={[0.1,0.1,0.1]}
        name="Object_0"

      >
            {/* <meshBasicMaterial  attach="material" map={texture}/>   */}
      </mesh>
      )
  }

  const FourFBX = () => {

    const mesh = useRef()
    let fbx = useFBX('/models/4.fbx')
    const { 
      gl,             
      canvas,              
    } = useThree()


    // useFrame(() => (mesh.current.rotation.x = mesh.current.rotation.y += 0.01))
      return (
        <mesh
        {...fbx.children[0]}
        ref={mesh}
        position={[0, 4, 6]}
        scale={[0.1,0.1,0.1]}
        name="Object_0"
        rotation={[0, 0, 0]}
      >
            {/* <meshBasicMaterial  attach="material" map={texture}/>   */}
      </mesh>
      )
  }

  const FiveFBX = () => {

    const mesh = useRef()
    let fbx = useFBX('/models/5.fbx')
    const { 
      gl,             
      canvas,              
    } = useThree()

    // useFrame(() => (mesh.current.rotation.x = mesh.current.rotation.y += 0.01))

      return (
        <mesh
        {...fbx.children[0]}
        ref={mesh}
        position={[-12, 0, 4]}
        scale={[0.1,0.1,0.1]}
        name="Object_0"
        rotation={[0, 0, 0]}
      >
            {/* <meshBasicMaterial  attach="material" map={texture}/>   */}
      </mesh>
      )
  }

  const SixFBX = () => {

    const mesh = useRef()
    let fbx = useFBX('/models/6.fbx')
    const { 
      gl,             
      canvas,              
    } = useThree()

    // useFrame(() => (mesh.current.rotation.x = mesh.current.rotation.y += 0.01))

      return (
        <mesh
        {...fbx.children[0]}
        ref={mesh}
        position={[-6, 0, 2]}
        scale={[0.1,0.1,0.1]}
        name="Object_0"
        rotation={[20, 20, 0]}
      >
            {/* <meshBasicMaterial  attach="material" map={texture}/>   */}
      </mesh>
      )
  }

  const UseFBXScene = () => {
    return (
      <>
          <OneFBX/>
          <TwoFBX/>
          <ThreeFBX/>
          <FourFBX/>
          <FiveFBX/>
          <SixFBX/>
        </>
    )
  }

  export default UseFBXScene
