import * as THREE from "three"
import { useRef, useState, Suspense, useEffect, useCallback } from 'react'
import { Canvas, useThree,useFrame} from "react-three-fiber"
import { OrbitControls  } from 'drei'
import { useFBX } from '@react-three/drei/useFBX'
import Layout from '../components/layout'
import { EffectComposer, DepthOfField, Bloom, Noise } from '@react-three/postprocessing'
import UseFBXScene from '../components/baby'
import Loader from '../components/loader'
import React from 'react'





function Bg() {
  const { viewport } = useThree()
  const max = Math.max(viewport.width, viewport.height)
  const ref = useRef()
  useFrame((state, delta) => (ref.current.time += delta))
  return (
    <mesh scale={[max, max, 1]}>
      <planeBufferGeometry />
      <colorShiftMaterial ref={ref} />
    </mesh>
  )
}




const Index = () => {

  const x = 0
  const y = 0
  const [z, updateZ] = useState(15)

  useEffect(()=>{
     updateZ(z+=1)
  })




  return (
   <Layout>
     <div className="three">


      <Canvas camera={{fov: 75, position: [x, y, z] }} 
        onCreated={()=>{console.log('loaded')}}
      >
        <ambientLight intensity={1.0} />
        <pointLight position={[40, 40, 40]} />
        {/* <OrbitControls enableZoom={false}/> */}
        <Suspense fallback={null}>
          <UseFBXScene/>    
        </Suspense>
        <EffectComposer>
        <DepthOfField focusDistance={20.0} focalLength={0.06} bokehScale={1.0} height={1200} />
        {/* <Bloom luminanceThreshold={bloomSize} luminanceSmoothing={0.5} height={800} /> */}
        <Noise opacity={0.25} />

      </EffectComposer>

      </Canvas>
     </div>
    </Layout>
  )
}

export default Index
