import * as THREE from "three"
import React from 'react'
import { useRef, useState, useEffect } from 'react'
import { useThree,useFrame, useLoader} from "react-three-fiber"
import { useFBX } from '@react-three/drei/useFBX'



const BabyFBX = ({setImage, isScreenshot, random}) => {
    const [babySnapshot, setBabySnapshot] = useState(null)
    let textures = ['crocodile','dragon', 'elephant', 'flowers', 'foil', 'freckle', 'fur1', 'fur2', 'fur3', 'giraffe1', 'giraffe2', 'hairy1', 'hairy2', 'kabk', 'leather', 'lizard', 'orange', 'pattern', 'peach', 'rhino', 'skin1', 'skin2', 'snake1', 'snake2','snake3', 'sponge', 'strawberry', 'tattoo1', 'tattoo2', 'veiny']
    const [texture, setTexture] = useState(useLoader(THREE.TextureLoader, `img/${textures[random]}.jpg`))
    const mesh = useRef()
    let fbx = useFBX('/baby6.fbx')
    const size = [7, (8- Math.random() * (1 -(-1)) + (-1)), 7]



    const { 
      gl,             
      canvas,              
    } = useThree()




    useEffect(()=>{
        setImage(babySnapshot)
    },[babySnapshot])


    useFrame((canvas) => {
          // if(isScreenshot){
            setBabySnapshot(canvas.gl.domElement.toDataURL())
          // }
    })


      return (
        <mesh
        {...fbx.children[0]}
        ref={mesh}
        position={[0, -2.5, 0]}
        scale={[7,7,7]}
        name="Object_0"
        rotation={[0, 0, 0]}
      >
            <meshBasicMaterial  attach="material" map={texture}/>  
      </mesh>
      )
  }

  const UseFBXScene = ({setImage, isScreenshot, random}) => {
    return (
    //   <Suspense fallback={null}>
        <BabyFBX setImage={setImage} isScreenshot={isScreenshot} random={random}/>
    //   </Suspense>
    )
  }

  export default React.memo(UseFBXScene)
